carrousel.initialize();function mover(index){index+=carrousel.numClones;var translateX=(carrousel.getElementWidth()+carrousel.marginRightItems)*index;translateX*=-1;carrousel.lista.style="transform : translate3d("+translateX+"px, 0, 0);"}
