function definir_altura_min(){carrousel.container.querySelector("ul").style.minHeight=carrousel.element.offsetHeight+"px"}
function onclicks(Evt){var botao=Evt.target;while(!botao.classList.contains("btn")){botao=botao.parentNode}
if(botao.getAttribute("data-target")==="previous"){carrousel.mostrarAnterior()}
else if(botao.getAttribute("data-target")==="next"){carrousel.mostrarProximo()}}
